import React from "react";
import styled from "styled-components";

import Netflix1 from "../images/Netflix1.png";
import Netflix2 from "../images/Netflix2.png";
import { FiRefreshCw } from "react-icons/fi";

const rankList = {
  Youtube: [
    {
      id: 1,
      rank: 1,
      src: "https://spnimage.edaily.co.kr/images/Photo/files/NP/S/2023/01/PS23012500038.jpg",
    },
    {
      id: 2,
      rank: 2,
      src: "https://talkimg.imbc.com/TVianUpload/tvian/TViews/image/2023/03/03/693cef21-b67a-4ce4-81c2-96d5ab8bb43b.jpg",
    },
  ],
};

export default function RecommendationSlider() {
  return (
    <div>
      <SlideBox>
        <>
          <BannerImg src={Netflix1} alt="Netflix1" />
        </>
        <>
          <BannerImg src={Netflix2} alt="Netflix2" />
        </>
        <>
          <BannerImg src={Netflix1} alt="Netflix1" />
        </>
        <>
          <BannerImg src={Netflix2} alt="Netflix2" />
        </>
        <>
          <BannerImg src={Netflix1} alt="Netflix1" />
        </>
        <>
          <BannerImg src={Netflix1} alt="Netflix1" />
        </>
        <RefreshButton>
          <RefreshDiv>
            <FiRefreshCw />
          </RefreshDiv>
        </RefreshButton>
      </SlideBox>
    </div>
  );
}

const SlideBox = styled.div`
  display: flex;
  width: 1200px;
  height: 300px;
  margin: 0px auto;

  /* background:white; */
  /* overflow:auto;
    white-space: nowrap; */
  /* overflow-x: scroll;
    scroll-snap-type: x mandatory; */
`;
const BannerImg = styled.img`
  height: 240px;
  width: 160px;
  border-radius: 20px;
  padding: 10px;
  cursor: pointer;
`;
const RefreshDiv = styled.div`
  font-size: 23px;
  color: white;
  cursor: pointer;
`;
const RefreshButton = styled.button`
  margin: 0px auto;
  background-color: transparent;
  color: white;
  height: 40px;
  align-self: center;
  /* border: none; */
  padding: 0px auto;
  font-size: 20px;
  border-radius: 10px;
  opacity: 0.5;
`;
