import React from "react";
import styled from "styled-components";

import Netflix1 from "../images/Netflix1.png";
import Netflix2 from "../images/Netflix2.png";
import { FiRefreshCw } from "react-icons/fi";

export default function RecommendationSlider() {
  return (
    <div>
      <SlideBox>
        <>
          <BannerImg src={Netflix1} alt="Netflix1" />
        </>
        <>
          <BannerImg src={Netflix2} alt="Netflix2" />
        </>
        <>
          <BannerImg src={Netflix1} alt="Netflix1" />
        </>
        <>
          <BannerImg src={Netflix2} alt="Netflix2" />
        </>
        <>
          <BannerImg src={Netflix1} alt="Netflix1" />
        </>
        <>
          <BannerImg src={Netflix1} alt="Netflix1" />
        </>
        <ScrollDiv>{FiRefreshCw}</ScrollDiv>
      </SlideBox>
    </div>
  );
}

const SlideBox = styled.div`
  width: 1400px;
  height: 400px;
  margin: 0px auto;

  /* background:white; */
  /* overflow:auto;
    white-space: nowrap; */
  /* overflow-x: scroll;
    scroll-snap-type: x mandatory; */
`;
const BannerImg = styled.img`
  height: 300px;
  width: 200px;
  border-radius: 20px;
  padding: 10px;
  cursor: pointer;
`;
const ScrollDiv = styled.div`
  font-size: 2500px;
  color: white;
  cursor: pointer;
`;
